sql.where:
o.status !=6 and i.status!=3

    and o.order_id=#{query.orderId}


    and u.username like '%${query.username}%'


    and o.status=#{query.status}

mapper.select:
SELECT
FROM gs_order o LEFT JOIN gs_order_shopping os ON o.order_id = os.order_id LEFT JOIN gs_order_item oi ON o.order_id=oi.order_id LEFT JOIN gs_user u ON o.user_id=u.id LEFT JOIN item i ON oi.item_id=i.id
ORDER BY o.gmt_create DESC LIMIT #{page.offset},#{page.rows}
